mapper.update:
update t_order
set order_name = #{orderName,jdbcType=VARCHAR},
  user_id = #{userId,jdbcType=INTEGER},
  student_id = #{studentId,jdbcType=INTEGER}
where Id = #{id,jdbcType=INTEGER}

mapper.select:
select Id, order_name, user_id, student_id
from t_order
where Id = #{id,jdbcType=INTEGER}
